  {title: "4. Data security", description: "", number: 7, phase_id: 17, modifiable: true, versionable_id: "479e38c4-f44f-4aa2-b3c3-2a3e99fa72e7"},
  {title: "5. Ethical aspects", description: "", number: 8, phase_id: 17, modifiable: true, versionable_id: "1337bd77-8a6e-4a7b-8fd7-3965f587fc4e"},
