  {title: "3. Allocation of resources", description: "<p>This section does not need to be completed for the preliminary DMP.</p>", number: 6, phase_id: 19, modifiable: true, versionable_id: "d498f1f3-8683-4594-8523-403878a03ce6"},
  {title: "5. Ethical aspects", description: "<p>This section does not need to be completed for the preliminary DMP.</p>", number: 8, phase_id: 19, modifiable: true, versionable_id: "eb2f69cc-4fcf-441d-926b-3d2d3ae53263"},
